  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Mobile Optimizations for PWA */
@media (max-width: 768px) {
  /* Prevent horizontal scrolling */
  body {
    overflow-x: hidden;
  }

  /* Container adjustments */
  .container {
    @apply px-3;
    max-width: 100%;
  }

  /* Better touch targets for mobile */
  button, [role="button"] {
    min-height: 48px;
    min-width: 48px;
    @apply text-sm px-4 py-3;
  }

  /* Viewport optimizations */
  .min-h-screen {
    min-height: 100vh;
    min-height: 100dvh;
  }

  /* Grid responsive adjustments */
  .grid.md\\:grid-cols-2.lg\\:grid-cols-3 {
    @apply grid-cols-1 gap-4;
  }

  /* Dialog content fits screen */
  .dialog-content {
    @apply max-w-[95vw] max-h-[90vh] overflow-y-auto;
  }

  /* Form improvements */
  .grid.grid-cols-2 {
    @apply grid-cols-1;
  }

  /* Text sizing adjustments */
  .text-2xl {
    @apply text-xl;
  }

  /* Header height adjustment */
  .h-16 {
    @apply h-14;
  }

  /* Improved mobile forms */
  input, textarea, select {
    font-size: 16px;
    @apply h-12 px-4 text-base;
  }
}

/* PWA specific styles */
@media (display-mode: standalone) {
  /* Hide elements that don't make sense in PWA mode */
  .web-only {
    display: none;
  }

  /* Add status bar spacing for PWA */
  body {
    padding-top: env(safe-area-inset-top);
  }
}

